//getting elements to display content
let prev_btn=document.querySelector(".but-2");
let next_btn=document.querySelector(".but-1");
let fact=document.querySelector(".fact");//displaying facts
let pic1=document.querySelector(".picture1");//displaying pictures
let pic2=document.querySelector(".picture2");
let middle;
let random;
let prev_fact;

//this function adds new facts on clicking next button
next_btn.addEventListener("click",function()
{
  prev_fact=middle;

 random=Math.floor(Math.random()*facts.length);

 middle=random;
 
fact.innerHTML=facts[random].fact;
pic1.src=facts[random].picture1;
pic2.src=facts[random].picture2;




})
//this function displays previous fact on clicking next button
prev_btn.addEventListener("click",function()
{


if(prev_fact==undefined){
  fact.innerHTML=facts[10].fact;
  pic1.src=facts[10].picture1;
pic2.src=facts[10].picture2;
}
else{
fact.innerHTML=facts[prev_fact].fact;
pic1.src=facts[prev_fact].picture1;
pic2.src=facts[prev_fact].picture2;
}

})


  
